$(document).ready(function() {

	$(".sharelist").each(function(i, el) {
		var jsdir = $("#js__script-main-bundle").attr("src");
		jsdir = jsdir.replace('/'+jsdir.split('/').pop(),"");
		$.get(
			jsdir+"/../../get_likes_count.php",
			{
				url: $(el).attr("data-url")
			},
			function(data) {
				$(el).find(".sn-mini__placeholder-sum").html(data.count);
				if (data!=null && data.count!=null && data.count!=0) $(el).find(".sn-mini__placeholder-sum").show();
			}
		);
	});

	$(".js-shares_btn").each(function(i, el) {
		el.addEventListener("click", function(event) {
			event.preventDefault();
			var url = el.href;
			var params = {
				width: 600,
				height: 360
			};
			var left = Math.round(screen.width/2 - params.width/2);
			var top = 0;
			if (screen.height > params.height) {
				top = Math.round(screen.height/3 - params.height/2);
			}

			var win = window.open(url, 'sl_' + this.service, 'left=' + left + ',top=' + top + ',' +
			   'width=' + params.width + ',height=' + params.height + ',personalbar=0,toolbar=0,scrollbars=1,resizable=1');
			if (win) {
				win.focus();
			}
			else {
				location.href = url;
			}
		}, false);
	});

	$("body").addClass("_load__page");
	$(".section, .paging").waypoint(function() {
		$(this).addClass("in-viewpoint");
	}, {
		offset: "100%"
	});

	$(".sn-mini").mouseenter(function() {
		var button = $(".sn-mini__button", $(this));
		$(this).find(".sn-pre").hide();
		$(this).find(".sn-post").css("height", "52px");
		button.not("._animate__button").each(function(i) {
			setTimeout(function() {
				button.eq(i).animate({top: "0px"}, 200);
			}, 50 * i);
		});
	});

	$(".sn-mini").mouseleave(function() {
		var button = $(".sn-mini__button", $(this));
		$(this).find(".sn-pre").show();
		$(this).find(".sn-post").css("height", "26px");
		button.not("._animate__button").each(function(i) {
			setTimeout(function() {
				button.eq(i).animate({top: "26px"}, 200);
			}, i);
		});
	});

	$.stellar({
		horizontalScrolling: false,
		verticalOffset: 0
	});

	if (!Modernizr.touch && false) {
		$(".zoom-image").elevateZoom({
			zoomType : "inner",
			cursor: "crosshair"
		});
	}

	$(".compare").twentytwenty();

});

