(function(){
	var singlePostManager = {
		_init: function(){
			var i,
				boxes = $('.js-postbox__annotation-pic'),
				bLen = boxes.length;

			if(bLen){
				for(i = 0; i < bLen; i++){
					(function(){
						var obj = {
							'imgs' : $(boxes[i]).find('.js-annotation-img'),
							'imgDescription' : $(boxes[i]).find('.js-postbox__annotation-pic__text_item'),
							'pager' : $(boxes[i]).find('.js-pic-pager__pic'),
							'arrowForward' : null,
							'arrowBack' : null
						};

						if(obj.imgs.length || obj.imgDescription.length){

							function imgDescrChangeVisible(index){
								obj.imgs.removeClass('active').item(index).addClass('active');
								obj.imgDescription.removeClass('postbox__annotation-pic__text_item_active').item(index).addClass('postbox__annotation-pic__text_item_active');
								obj.pager.text(index + 1);
							}

							obj.arrowForward = $(boxes[i]).find('.js-pic-pager__arrow_forward');
							obj.arrowBack = $(boxes[i]).find('.js-pic-pager__arrow_back');
							obj.arrowForward.click(function(){
								var index = obj.imgs.index(obj.imgs.filter('.active')) + 1;
								if(index >= obj.imgs.length){
									index = 0;
								}
								imgDescrChangeVisible(index);
							});

							obj.arrowBack.click(function(){
								var index = obj.imgs.index(obj.imgs.filter('.active')) - 1;
								if(index < 0){
									index = obj.imgs.length - 1;
								}
								imgDescrChangeVisible(index);
							});
						}
					}());
				}
			}
		}
	};
	singlePostManager._init();
}());

var validateEmail = function(email) {

	var re = /^(\w+)([\-+.\'][\w]+)*@(\w[\-\w]*\.){1,5}([A-Za-z]){2,6}$/;
	var mail_check = re.test(email) || false;

	return mail_check;
};

$(document).ready(function() {

	var messageError = function (a){

		var wrap = $('.subscribe-form__field__input');
			holder = $('.error-holder', wrap);

		if(a){
			holder.text('');
			holder.text(a);
			wrap.addClass('error');
		}
		else{
			holder.text('');
			wrap.removeClass('error');
		}
	};

	var onDataPosted = function(data) {
		if (data.success) {
			messageError('<div class="lead">Благодарим вас!</div><p>На <b>' + data.mail + '</b> было выслано сообщение об активации подписки.</p><p>Чтобы завершить процедуру пройдите по ссылке в сообщении.</p>');
			$('.subscribe-form__input').val('');
		}
		else {
			if (!data.success && data.repeat){
				messageError('Такой e-mail уже зарегестрирован');
			}
			else if(!data.success){
				messageError('Проверить правильность введенного адреса');
			}
			else{
				messageError('К сожалению произошла ошибка, попробуйте снова');
			}
		};
	};

	$('.subscribe-form__submit').click(function() {

		var email = $('.subscribe-form__input').val();

		if (validateEmail(email)) {
			var jsdir = $("#js__script-main-bundle").attr("src");
			jsdir = jsdir.replace('/'+jsdir.split('/').pop(),"");
			$.post(jsdir+'/../../subscribe/post_subscribe-form.php', { email: email }, onDataPosted);
		}
		else{
			if(email == ''){
				messageError('Введите e-mail');
			}
			else{
				messageError('Указан некорректный e-mail');
			}

		}
	});

	var $input = $('.subscribe-form__input');
	$input.focus(function() {
		messageError()
	});

	if ( !('placeholder' in document.createElement('input')) ) {

		var placeholder = $input.attr('placeholder');

		$input.focus(function() {
			if ($input.val() == placeholder) {
				$input.val('');

			};
		});
		$input.blur(function()  { if ($input.val() == '') { $input.val(placeholder); }; });
		$input.blur();

		//$('#searchform').submit(function() { if ($input.val() == placeholder) { return false; } });
	};
});

